mapper.select:

select * from 
	(
	select qna.*,rownum rnum from 
		(
		select * from qna order by qnaNum desc
		) qna
	) 
 where rnum >= #{startRow} and rnum <=#{endRow}

select * from 
	(
	select aa.*,rownum rnum from 
		(
		select * from qna

				where qnaTitle like '%'||#{qnaTitle}||'%'

		order by qnaNum desc
		) aa
	) 
 where rnum >= #{startRow} and rnum <= #{endRow}
