mapper.select:
select * from
(
	select aa.*, rownum rnum from
	(
		select * from branch

		order by branchnum desc
	)aa
)
where rnum>=#{startRow} and rnum<=#{endRow}

select r.outcome,r.ticketIncome,r.storeIncome,to_char(r.revenueDate,'YYYY-MM-DD') revenueDate
from branch b,revenue r where b.branchNum=r.branchNum and 
brName = #{brName}
